mapper.select:
select * from (select rank() over(order by user_idx desc) no, users.* from (select * from users
user_idx like '%'||#{user_idx}||'%' or user_id like '%'||#{user_id}||'%' or user_name like '%'||#{user_name}||'%' or user_address like '%'||#{user_address}||'%'
)users ) where no between #{start} and #{end}

select * from (select rank() over(order by user_idx desc) no, sitter.* from (select * from sitter
user_idx like '%'||#{user_idx}||'%' or user_name like '%'||#{user_name}||'%' or sitter_title like '%'||#{sitter_title}||'%' or sitter_city like '%'||#{sitter_city}||'%'
)sitter ) where no between #{start} and #{end}

select * from ( select rank() over(order by payment_idx desc) no, payment.payment_idx, payment.payment_reserve_idx, payment.payment_user_idx, payment.payment_sitter_idx, payment.payment_user_name, payment.payment_sitter_name, payment.payment_uid, payment.payment_amount, payment.payment_method, payment.payment_status, payment.payment_date from payment
payment_user_name like '%' || #{payment_user_name} || '%'
or payment_sitter_name like '%' || #{payment_sitter_name} || '%'
or payment_uid like '%' || #{payment_uid} || '%'
) payment where no between #{start} and #{end}

SELECT * FROM (
    SELECT rank() over (order by notice_regdate DESC) as no, o.*
    FROM notice o
)
WHERE no BETWEEN #{start} AND #{end}

mapper.update:
update notice set
notice_readhit = notice_readhit + 1
where notice_idx = #{notice_idx}

UPDATE notice
SET
    notice_title=#{notice_title},
    notice_name=#{notice_name},
    notice_content=#{notice_content}
WHERE notice_idx=#{notice_idx}
